import React, { useEffect, useState } from "react";
import { API } from "./api/api";

function App() {
  let [items, setItems] = useState([]);
  let [offset, setOffset] = useState(0);
  let result = {};

  useEffect(() => {
    API.getItemIds(offset)
      .then((data) => API.getItems(data.data.result))
      .then((data) => {
