import { Card, CardContent, CardMedia, Grid, Typography } from "@mui/material";
import React from "react";

const GoodsItem = ({ item }) => {
  const { brand, id, price, product } = item;
  return (
    <Grid item xs="12" sm="6" md="4" sx={{ border: "1px solid grey" }}>
      <Card>
        <CardMedia
          component="img"
          image="ValantisHolder.png"
          alt={product}
          height="140"
        />
        <CardContent>
          <Typography variant="h6" component="h5">{id}</Typography>
          <h5 className="card-title">{product}</h5>
          <p className="card-price">Цена: {price} руб.</p>
          <p className="card-brand">{brand || "Made by Valantis"}</p>
        </CardContent>
      </Card>
    </Grid>
  );
};

export default GoodsItem;
