import axios from "axios";
import { secretKey } from "./apiKey";

const instance = axios.create({
  baseURL: "https://api.valantis.store:41000/",
  headers: { "X-Auth": secretKey },
  
});
let data = JSON.stringify({
  action: "get_ids",
  params: {
    offset: 0,
    limit: 50,
  },
});
export const API = {
  getItem() {
    return instance.post(data).then((res)=>{
        console.log(res)
    })
    // return instance.post(data).then((res) => {
    //   console.log(res);
    // });
  },
};
