import "./App.css";
import React, { useEffect, useState } from "react";
import { API } from "./api/api";
import Header from "./components/Header";
import Context from "./Context/Context";
import { Container, Pagination } from "@mui/material";
import GoodsList from "./components/GoodsList/GoodsList";
import SearchForm from "./components/SearchForm/SearchForm";
import Preloader from "./components/Preloader/Preloader";
function App() {
  const [items, setItems] = useState([]);
  const [brands, setBrands] = useState([]);
  const [offset, setOffset] = useState(0);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [qtyItems, setQtyItems] = useState(0);
  const pages = Math.ceil(qtyItems / 50);

  useEffect(() => {
    let sortBrand;
    let product;
    API.getItemIds(offset)
      .then((data) => {
        setLoading(true);
        return API.getItems(data.data.result);
      })
      .then((data) => {
        (() => {
          product = [
            ...new Map(data.data.result.map((el) => [el.id, el])).values(),
          ];
        })();

        setItems(product);
      });
    API.getQtyItems().then((data) => {
      setQtyItems(data.data.result.length);
    });
    // API.getFilter(search)
    //   .then((data) => {
    //     return API.getItems(data.data.result);
    //   })
    //   .then((data) => {
    //     (() => {
    //       product = [
    //         ...new Map(data.data.result.map((el) => [el.id, el])).values(),
    //       ];
    //     })();
    //     setItems(product);
    //   });
    // API.getFields().then((data) => {
    //   (() => {
    //     sortBrand = data.data.result.reduce((acc, item) => {
    //       if (acc.includes(item)) {
    //         return acc;
    //       }
    //       return [...acc, item];
    //     }, []);
    //     setBrands(sortBrand);
    //   })();
    // });
    // (async () => {
    //   const response = await API.getItemIds(offset);
    //   const itemsResponse = await API.getItems(response.data.result);
    //   setItems(itemsResponse.data.result);
    // })();
  }, [offset, search]);

  const handleChange = (e) => {
    e.preventDefault();
    if (!e.target.value) {
      setItems(items);
      setSearch("");
      return;
    }
    setSearch(e.target.value);
  };
  const valueContext = {
    items,
    setOffset,
    brands,
    search,
    handleChange,
  };

  return (
    <Context.Provider value={valueContext}>
      <Header />

      {loading && items.length === 0 ? (
        <Preloader />
      ) : (
        <Container>
          <SearchForm />
          <GoodsList />
          {/* <pre>{JSON.stringify(items, null, 2)}</pre>/ */}
          {/* <PaginationContainer /> */}
          <Pagination
            count={pages}
            onChange={(_) => setOffset(50)}
          />
        </Container>
      )}
    </Context.Provider>
  );
}

export default App;
