import "./App.css";
import React, { useEffect, useState } from "react";
import { API } from "./api/api";
import Header from "./components/Header";
import Context from "./Context/Context";
import PaginationContainer from "./components/Pagination/PaginationContainer";
import { Container } from "@mui/material";
import GoodsList from "./components/GoodsList/GoodsList";
import SearchForm from "./components/SearchForm/SearchForm";
import { secretKey } from "./api/apiKey";
function App() {
  let [items, setItems] = useState([]);
  let [brands, setBrands] = useState([]);
  let [offset, setOffset] = useState(0);
  const [search, setSearch] = useState("");

  useEffect(() => {
    let sortBrand;
    let product;
    API.getItemIds(offset)
      .then((data) => {
        return API.getItems(data.data.result);
      })
      .then((data) => {
        (() => {
          product = [
            ...new Map(data.data.result.map((el) => [el.id, el])).values(),
          ];
        })();
        setItems(product);
      });

    // API.getFields().then((data) => {
    //   (() => {
    //     sortBrand = data.data.result.reduce((acc, item) => {
    //       if (acc.includes(item)) {
    //         return acc;
    //       }
    //       return [...acc, item];
    //     }, []);
    //     setBrands(sortBrand);
    //   })();
    // });
    // (async () => {
    //   const response = await API.getItemIds(offset);
    //   const itemsResponse = await API.getItems(response.data.result);
    //   setItems(itemsResponse.data.result);
    // })();
  }, [offset]);
  const [filterReqBody, setFilterReqBody] = useState({})

  const onFormSubmit = (e) => {
    e.preventDefault()
    const formData = new FormData(e.target)
    const param = formData.get("param").toLowerCase()
    let value = formData.get("value")
    if (param === "price") value = Number(value)
    const filterReqBody = {
      "action": "filter",
      "params": {}
    }
    filterReqBody["params"][param] = value
    setFilterReqBody(filterReqBody)
  }
  const [isLoading, setIsLoading] = useState(false)
  const BASE_URL = 'https://api.valantis.store:41000'
  const headers = {
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Headers": "*",
    "Access-Control-Allow-Credentials": true,
    "Content-Type": "application/json;charset=utf-8",
    "X-Auth": secretKey
  }
  useEffect(() => {
    const fetchIds = async () => {
      setIsLoading(true)

      try {
        const response = await fetch(`${BASE_URL}`, {
          headers: headers,
          method: "POST",
          body: JSON.stringify(filterReqBody)
        })
        .then(res => {
          if (!res.ok) {
            throw new Error(`${res.status}`)
          }
          return res.json()
        })

        const ids = response.result
        setIds(ids)
      } catch (error) {
        console.log(error)
        if (error.message === '500') {
          fetchIds()
        }
        setIsLoading(false)
      }
    }
    if (Object.keys(filterReqBody).length) fetchIds()
  }, [filterReqBody])
  const handleChange = (e) => {
    if (!e.target.value) {
      setItems(items);
      setSearch("");
      return;
    }
    setSearch(e.target.value);
    setItems(
      items.filter((item) => {
        return item.product
          .toLowerCase()
          .includes(e.target.value.toLowerCase());
      })
    );
  };
  const valueContext = {
    items,
    setOffset,
    brands,
    search,
    handleChange,
  };

  return (
    <Context.Provider value={valueContext}>
      <Header />
      <Container>
        <SearchForm />
        <GoodsList />
        {/* <pre>{JSON.stringify(items, null, 2)}</pre>/ */}
        <PaginationContainer />
      </Container>
    </Context.Provider>
  );
}

export default App;
