import axios from "axios";
import { secretKey } from "./apiKey";
import { useState } from "react";

import React from 'react'

const api = () => {
  return (
    
    <div>
      
    </div>
  )
}

export default api

let getIds = JSON.stringify({
  action: "get_ids",
  params: {
    offset: 0,
    limit: 3,
  },
});
let getItems = JSON.stringify({
  action: "get_items",
  params: { ids: ["1789ecf3-f81c-4f49-ada2-83804dcc74b0"] },
});
const instance = axios.create({
  baseURL: "https://api.valantis.store:41000",
  headers: { "X-Auth": secretKey, "Content-Type": "application/json" },
});

export const API = {
  getItemIds() {
    return instance
      .post("/", getIds)
      .then((res) => {
        return res.data;
      })
      .catch((err) => {
        console.error(err);
      });
  },
};
